var account, _ = cmm.FromHexKey("89A0F0E0732ACAA7AD37C9E6D7A9798ECCE6940C63FF0290A58B1C1C1697486A")
var receiveAccount = common.HexToAddress("0xaa25Aa7a19f9c426E07dee59b12f944f4d9f1DD3")
var tokenhubABI, _ = abi.JSON(strings.NewReader(TokenhubABI))

func TestSimulateHandleBind(t *testing.T) {
	client, err := ethclient.Dial(cmm.Endpoint)
	assert.NoError(t, err)

	nonce, err := client.PendingNonceAt(context.Background(), account.Addr)
	assert.NoError(t, err)

	gasPrice, err := client.SuggestGasPrice(context.Background())
	assert.NoError(t, err)

	auth := bind.NewKeyedTransactor(account.Key)
	auth.Nonce = big.NewInt(int64(nonce))
	auth.Value = big.NewInt(0)
	auth.GasPrice = gasPrice
	auth.GasLimit = 4700000

	value, _ := hex.DecodeString("000000000000000000000000000000000000000000000000030885f52d3e2c00000000000000000000000000000000000000000053a9e6a64992ffce817b7e973a8df704b5058f300002")
	proofBytes, _ := hex.DecodeString("")

	data, err := tokenhubABI.Pack("handleRefundPackage", value, proofBytes, uint64(2295), uint64(0))
	assert.NoError(t, err)

	msg := ethereum.CallMsg{
		From:     auth.From,
		To:       &cmm.TokenHub,
		Gas:      auth.GasLimit,
		GasPrice: auth.GasPrice,
		Value:    auth.Value,
		Data:     data,
	}

	res, err := client.CallContract(context.Background(), msg, nil)

	fmt.Println(err)
	fmt.Println(string(res))
}

func TestSimulateTransferOut(t *testing.T) {
	client, err := ethclient.Dial(cmm.Endpoint)
	assert.NoError(t, err)

	nonce, err := client.PendingNonceAt(context.Background(), account.Addr)
	assert.NoError(t, err)

	gasPrice, err := client.SuggestGasPrice(context.Background())
	assert.NoError(t, err)

	auth := bind.NewKeyedTransactor(account.Key)
	auth.Nonce = big.NewInt(int64(nonce))
	auth.Value = big.NewInt(10000000000000000)
	auth.GasPrice = gasPrice
	auth.GasLimit = 4700000

	//value, _ := hex.DecodeString("")
	//proofBytes, _ := hex.DecodeString("")

	contractAddr := common.HexToAddress("0xEdC88D3aD1Eb3C148814Bc75A23ec772111d7085")
	recipientAddr := common.HexToAddress("0x68655acb135a8706ac89e451e3efed732c5e838b")
	data, err := tokenhubABI.Pack("transferOut", contractAddr, recipientAddr, big.NewInt(5000000000000000000), big.NewInt(1597543193), big.NewInt(10000000000000000))
	assert.NoError(t, err)

	msg := ethereum.CallMsg{
		From:     auth.From,
		To:       &cmm.TokenHub,
		Gas:      auth.GasLimit,
		GasPrice: auth.GasPrice,
		Value:    auth.Value,
		Data:     data,
	}

	res, err := client.CallContract(context.Background(), msg, nil)

	fmt.Println(err)
	fmt.Println(string(res))
}
